example.code:
String[] goods = { "coffee", "flour", "tea"};
float[] amounts = { 108.6, 5.8, 8.2 };

FloatDict inventory;

void setup() {
  size(200, 200);
  inventory = new FloatDict(goods, amounts);
  println(inventory);  // There is 8.2 of tea
  inventory.div("tea", 2);
  println(inventory);  // There is 4.1 of tea
}
