example.code:
String[] goods = { "coffee", "flour", "tea"};
float[] amounts = { 108.6, 5.8, 8.2 };

FloatDict inventory;

void setup() {
  size(200, 200);
  inventory = new FloatDict(goods, amounts);
  println(inventory);
  inventory.set("coffee", 12.4);  // Less coffee
  inventory.set("sugar", 5.8);   // Add sugar
  println(inventory);
}
