example.code:
PShape s;  // The PShape object

void setup() {  
  size(100, 100, P2D);
  // Creating the PShape as a square. The corner 
  // is 0,0 so that the center is at 40,40 
  s = createShape();
  s.beginShape();
  s.fill(0, 0, 255);
  s.noStroke();
  s.vertex(0, 0);
  s.vertex(0, 50);
  s.vertex(50, 0);
  s.endShape();
}

void draw() {
  shape(s, 25, 25);
}
