example.code:
String[] goods = { "coffee", "flour", "tea"};
String[] amounts = { "black", "white", "green" };

StringDict inventory;

void setup() {
  size(200, 200);
  inventory = new StringDict(goods, amounts);
  println(inventory);
  noLoop();
  fill(0);
  textAlign(CENTER);
}

void draw() {
  String s = inventory.get("tea");
  text(s, width/2, height/2);
}
