example.code:
String[] goods = { "coffee", "flour", "tea"};
StringList inventory;

void setup() {
  size(200, 200);
  inventory = new StringList(goods);
  println(inventory);
  noLoop();
  fill(0);
  textAlign(CENTER);
}

void draw() {
  String item = inventory.get(2);
  text(item, width/2, height/2);
}
