example.code:
size(100, 100, P2D);
translate(50, 50);
stroke(255, 0, 0);
beginShape();
// Exterior part of shape
vertex(-40, -40);
vertex(40, -40);
vertex(40, 40);
vertex(-40, 40);
// Interior part of shape
beginContour();
vertex(-20, -20);
vertex(20, -20);
vertex(20, 20);
vertex(-20, 20);
endContour();
endShape(CLOSE);
