example.code:
PImage img;

void setup() {
  img = loadImage("laDefense.jpg");
}

void draw() {
  image(img, 0, 0);
}

PImage webImg;

void setup() {
  String url = "http://processing.org/img/processing_cover";
  // Load image from a web server
  webImg = loadImage(url, "gif");
}

void draw() {
  background(0);
  image(webImg, 0, 0);
}
