example.code:
// Load an image with setup() and draw()

PImage b;

void setup() {
  b = loadImage("laDefense.jpg");
  noLoop();
}

void draw() {
  image(b, 0, 0);
}

// Load an image over a network

PImage online;

void setup() {
  String url = "http://processing.org/img/processing_cover.gif";
  online = loadImage(url, "png");
  noLoop();
}

void draw() {
  image(online, 0, 0);
}
