example.code:
PFont font;
font = loadFont("Sabon-Roman-32.vlw");
textFont(font);

float base = height * 0.75;

textSize(32);  // Set initial text size
float d = textDescent();  // Calc descent
line(0, base+d, width, base+d);
text("dp", 0, base);  // Draw text on baseline

textSize(60);  // Increase text size
d = textDescent();  // Recalc descent
line(40, base+d, width, base+d);
text("dp", 40, base);  // Draw text on baseline
