example.code:
PImage img;
img = loadImage("laDefense.jpg");
image(img, 0, 0);
tint(0, 153, 204);  // Tint blue
image(img, 50, 0);

PImage img;
img = loadImage("laDefense.jpg");
image(img, 0, 0);
tint(0, 153, 204, 126);  // Tint blue and set transparency
image(img, 50, 0);

PImage img;
img = loadImage("laDefense.jpg");
image(img, 0, 0);
tint(255, 126);  // Apply transparency without changing color
image(img, 50, 0);
